parameters.jsCode:
const destination = $input.item.json.body.destination;
const startDate = $input.item.json.body.startDate;
const endDate = $input.item.json.body.endDate;
const interests = $input.item.json.body.interests;
const landmarks = $input.item.json.body.landmarks || [];

// Calculate days
const start = new Date(startDate);
const end = new Date(endDate);
const days = Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;

// Filter landmarks by destination
const destinationLandmarks = landmarks.filter(l => 
  destination.toLowerCase().includes(l.city.toLowerCase()) || 
  destination.toLowerCase().includes(l.country.toLowerCase())
);

return {
  destination,
  startDate,
  endDate,
  interests: interests.join(', '),
  days,
  destinationLandmarks: JSON.stringify(destinationLandmarks)
};

