const aiResponse = $input.item.json.response || $input.item.json.message?.content || '{}';
const parsedAI = JSON.parse(aiResponse);
const inputData = $('Extract Request Data').item.json;

// Build final response
const result = {
  destination: inputData.destination,
  description: parsedAI.description,
  days: inputData.days,
  weather: parsedAI.weather,
  packing: parsedAI.packing,
  itinerary: parsedAI.itinerary.map((day, index) => ({
    day: index + 1,
    title: day.title,
    date: new Date(new Date(inputData.startDate).getTime() + index * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    weather: parsedAI.weather.temperature.split('-')[0] + 'Â°C',
    activities: day.activities.map(act => ({
      ...act,
      mapLink: `https://www.google.com/maps/search/${encodeURIComponent(act.location + ' ' + inputData.destination)}`
    }))
  }))
};

return result;
